import React, { useState, useEffect } from 'react';
import { Spinner } from 'components/shared/Loader';
import { Inner, TextContent, TextID, Wrapper, WrapperText, SearchButton, InputSearch, Fun, InputContainer, IconScrh, LabelFormed, ClearButtoned } from './UsersList.styled';
import pi from '../../../../../assets/icons/pict.svg';

type Prop = {
  type?: string;
  isShowUsers?: number | null;
};

export type DataProp = {
  id: string;
  lastname: string;
  name: string;
}

export const UsersList: React.FC<Prop & { searchQuery?: string, onSearch: (query: string) => void }> = ({ type, isShowUsers, searchQuery, onSearch }) => {
  const [data, setData] = useState<DataProp[]>([]); // State to hold the fetched data
  const [isLoading, setIsLoading] = useState(true);
  const [isSearchClicked, setIsSearchClicked] = useState(false);

  useEffect(() => {
    // Fetch data from the API when the component mounts
    fetch('https://server-your-price-booking.onrender.com/api/acsess-to-admin/get-all-users')
      .then(response => response.json())
      .then(user => {
        const userData: DataProp = {
          id: user.id,
          lastname: user.lastname,
          name: user.name
        };
        setData([userData]);
        setIsLoading(false); // Mark loading as complete

      })
      .catch(error => {
        console.error('Error fetching data:', error);
        setIsLoading(false); // Mark loading as complete, even on error
      });
  }, []);
  console.log('Data:', data);
  const handleSearchClick = () => {
    setIsSearchClicked(true);

  };

  const handleClearClick = () => {
    setIsSearchClicked(false); // Reset the search flag when the input is cleared
    onSearch(''); // Clear the search query
  };

  const filteredData = searchQuery
  ? data.filter((item) => item && item.name && typeof item.name === 'string' && item.name.toLowerCase().includes(searchQuery.toLowerCase()))
  : data;

  const displayData = isSearchClicked ? filteredData : data;

  const firstName = data[0].name;
  return (
    <Wrapper>
      <Fun>
        <InputContainer>
          <InputSearch
           className="input"
           type="text"
           placeholder="Search ID Hoteliers or/and Name object"
           value={searchQuery}
           onChange={(e) => onSearch(e.target.value)}
           autoComplete="search" // Add an appropriate autocomplete value
          />
          <LabelFormed className="label" htmlFor="input-id">Search ID Hoteliers or/and Name object</LabelFormed>
          <IconScrh src={pi} alt="Search" />
          {searchQuery && (
            <ClearButtoned type="button" onClick={handleClearClick}>Close</ClearButtoned>
          )}
          <SearchButton onClick={handleSearchClick}>Search</SearchButton>
        </InputContainer>
      </Fun>

      {displayData.length > 0 && 
  displayData.map(({ lastname, name, id }, index) => (
    <Inner
      key={id}
      border={isShowUsers === index}
      hidden={isShowUsers !== index && isShowUsers !== null}
      position={isShowUsers !== null}
      tabIndex={index}
    >
      <TextID tabIndex={index}>ID {id}</TextID>
      <WrapperText tabIndex={index} typePage={type}>
        <TextContent tabIndex={index}>
          {isLoading ? <Spinner /> : (name && lastname) || `${"unluck"} ${"unluck"}`}
        </TextContent>
      </WrapperText>
    </Inner>
  ))
}
    </Wrapper>
  );
}
