import { ReactSVG } from "react-svg";
import { useState } from "react";
import { StatisticsHeader } from "../StatisticsHeader/StatisticsHeader";
import { Wrapper, WrapperArrow } from "./Hoteliers.styled";
import { HoteliersList } from "./HoteliersList/HoteliersList";
import arrow from '../../../../assets/icons/arrowHoteliers.svg'

type Prop = {
  page?: string;
};

export const Hoteliers: React.FC<Prop> = ({ page }) => {
  const [isShowHotelier, setIsShowHotelier] = useState<number | null>(null);
     const [isClicked, setIsClicked] = useState<boolean>(false);
     const [searchText, setSearchText] = useState<string>("");
  const [searchResults, setSearchResults] = useState<any[]>([]);
    const hoteliersData = [
      { id: 1, name: "Hotel A" },
      { id: 2, name: "Hotel B" },
      { id: 3, name: "Hotel C" },
    ];
    const closeAccount = () => {
      setIsShowHotelier(null);
      setIsClicked(false)
    };
    // const handleSearch = (event: React.ChangeEvent<HTMLInputElement>) => {
    //   const query = event.target.value;
    //   setSearchQuery(query);
    // };
    const handleSearchChange = (e: React.ChangeEvent<HTMLInputElement>) => {
      setSearchText(e.target.value);
    };

    const handleSearchClick = () => {
      // Implement your search logic here and update searchResults
      // For this example, let's filter results based on a simple condition (e.g., if the name includes the search text)
      const filteredResults = hoteliersData.filter((hotelier) =>
        hotelier.name.includes(searchText)
      );
      setSearchResults(filteredResults);
    };
  return (
    <Wrapper padding={typeof isShowHotelier === 'number'}>
      {typeof isShowHotelier === 'number' && (
        <WrapperArrow onClick={closeAccount}>
          <ReactSVG src={arrow} />
        </WrapperArrow>
      )}
      <StatisticsHeader type="Hoteliers" isShowHotelier={isShowHotelier} />
      {/* <input
        type="text"
        placeholder="Search Hoteliers"
        value={searchQuery}
        onChange={handleSearch}
      /> */}
      <div>
        <input
          type="text"
          placeholder="Search Hoteliers"
          value={searchText}
          onChange={handleSearchChange}
        />
        <button type="button" onClick={handleSearchClick}>
          Search
        </button>
      </div>

      {searchResults.length > 0 ? (
        <ul>
          {searchResults.map((hotelier) => (
            <li key={hotelier.id}>{hotelier.name}</li>
          ))}
        </ul>
      ) : (
        <p>No results found.</p>
      )}
      <HoteliersList
        type={page}
        isShowHotelier={isShowHotelier}
        setIsShowHotelier={setIsShowHotelier}
        isClicked={isClicked}
        setIsClicked={setIsClicked}
      />
    </Wrapper> 
  );
      };
