import React, { useState } from 'react';
import { setToken } from 'features/access/tokenSlice'; 
import { useDispatch } from 'react-redux'; 
import axios, { AxiosResponse } from 'axios'; 

const LoginPage = () => {
  const [email, setEmail] = useState('');
  const [verificationCode, setVerificationCode] = useState('');
  const [isCodeSent, setIsCodeSent] = useState(false);
  const [userRole] = useState<string | null>(null);
  const [userToken, setUserToken] = useState<string | null>(null);
  
  type ResponseData = {
    status: string;
    data: {
      token: string;
    };
  }
  const dispatch = useDispatch();
  const validPassword = async (): Promise<boolean> => {
    try {
      const response: AxiosResponse<ResponseData> = await axios.post(
        'https://server-your-price-booking.onrender.com/api/acsess-to-admin/log-in-admin/',
        {
          email: email,
          secretCode: verificationCode,
        }
      );

      const { status, data } = response.data;
dispatch(setToken(data.token));
setUserToken(data.token);
      return status === 'success';
    } catch (error) {
      console.error('Помилка під час перевірки секретного коду', error);
      return false;
    }
  };
  const sendVerificationCode = async () => {
   if(!(await validPassword())){
    console.log('error');
   }
   else{
    setIsCodeSent(true);
   }
  };
  const verifyCode = async () => {
    try {
      if (userRole === 'superadmin') {
        console.log('Super Admin Access');
        setUserToken('eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjY0NWJmOGI1ZWM0NGM0MTY1NDcxNmMzZSIsImlhdCI6MTY5ODA4OTM4OCwiZXhwIjoxNjk4MDkyOTg4fQ.OMlT1T17U4DgdsgbXs8JXmMG5Wb9ECuFL_UjAhibXjI');
      } else if (userRole === 'admin') {
        console.log('Admin Access');
        setUserToken('eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjY1MzZjYjQwNTc4YzQwZTM1NGZhZDU0ZCIsImlhdCI6MTY5ODMwNzM5MywiZXhwIjoxNjk4MzEwOTkzfQ.TCMVGYiMMW5kwrAWyxblT98fTcXAT8CPA2rJADZYUZw');
      } else {
        console.error('Invalid user role');
      }
    } catch (error) {
      console.error('Code verification failed');
    }
  };
console.log('s',userToken);
  return (
    <div>
      {isCodeSent ? (
        <div>
          <label>Email:
            <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} />
          </label>
          <label>Verification Code:
            <input type="text" value={verificationCode} onChange={(e) => setVerificationCode(e.target.value)} />
          </label>
          <button type="button" onClick={verifyCode}>Verify Code</button>
        </div>
      ) : (
        <div>
          <label>Email:
            <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} />
          </label>
          <button type="button" onClick={sendVerificationCode}>Send Verification Code</button>
        </div>
      )}
    </div>
  );
};

export { LoginPage };
