import styled from 'styled-components/macro';


type PropsInner = {
  border: boolean;
  position?: boolean | undefined;
};

type Props = {
  tabIndex: number;
};

type PropsWrapper = {
  tabIndex: number;
  typePage?: string | undefined;
};

export const Wrapper = styled.ul`
  display: flex;
  flex-direction: column;
  gap: 3px;

  cursor: pointer;
`;export const InputContainer = styled.div`
display: flex;
align-items: center;
`;
export const Fun = styled.div`
display:flex;
flex-direction:row;

`
export const lblForm = styled.label`
display:none;
position:absolute;
width: max-content;
font-size:12px;
top: 2%; /* Center vertically */
padding: 3px 4px;
left: 50%;
transform: translateX(-50%);
right: 0;
z-index: 1;
opacity: .4;
`
export const IconScrh = styled.img`
position: absolute;
left: 80px;
width: 20px;
height: 20px;
`;
export const InputSearch = styled.input`
width: 558px;
height: 38px;
background: linear-gradient(0deg, #D0D0D0, #D0D0D0),
            linear-gradient(0deg, #F4F4F4, #F4F4F4);
border: 0.3px solid #D0D0D0;
border-radius: 50px;
color: #4C4C4C;
cursor: pointer;
padding: 5px 10px; 
text-align: center;
  position: relative;
font-size:12px;
font-weight:400;
&::placeholder {
  color: #AAA;
  font-size: 14px;
  position: absolute;
  top: 50%; /* Center vertically */
  left: 0;
  right: 0;
  transform: translateY(-50%);
  transition: top 0.2s, font-size 0.2s; /* Smooth transition for top and font-size */
`
export const SearchButton = styled.button`
width: 108px;
height: 38px;
background-color: #F3F651;
border: 0.3px solid #0090FF; 
border-radius: 50px; 
color: #0090FF; 
Top:399px;
Left:768px;
font-weight: bold; 
cursor: pointer;
margin-left:28px;
`;
export const Inner = styled.li.attrs<Props>(({ tabIndex }) => ({
  'data-id': tabIndex,
}))<PropsInner>`
  display: ${({ hidden }) => (hidden ? 'none' : 'flex')};
  align-items: ${({ position }) => (position ? 'flex-start' : 'center')};

  width: 100%;
  padding: 12px 31px 12px 11px;

  background-color: ${({ theme }) => theme.colors.white};
  border: ${({ border }) => (border ? '0.7px' : '0.3px')} solid
    ${({ border, theme }) => (border ? theme.colors.black : theme.colors.border.borderList)};

  &:hover {
    border: 0.4px solid ${({ theme }) => theme.colors.border.btnBorderHover};
  }
  &:active {
    border: 1px solid ${({ theme }) => theme.colors.border.borderActive};

  }

`;

export const TextID = styled.p.attrs<Props>(({ tabIndex }) => ({
  'data-id': tabIndex,
}))`
  white-space: nowrap;

  font-family: ${({ theme }) => theme.fonts.segoe};
  font-weight: 600;
  font-size: 12px;
  line-height: 16px/12px;
  text-transform: uppercase;
  color: ${({ theme }) => theme.colors.textThird};
`;

export const WrapperText = styled.div.attrs<PropsWrapper>(({ tabIndex }) => ({
  'data-id': tabIndex,
}))<{ typePage?: string }>`
  display: flex;
  gap: 13px;

  width: 77%;
  white-space: ${({ typePage }) => (typePage && typePage === 'Finance' ? 'wrap' : 'nowrap')};
  overflow: hidden;

  margin-left: 26px;
`;

export const TextContent = styled.div.attrs<Props>(({ tabIndex }) => ({
  'data-id': tabIndex,
}))`
display: inline-block
  font-family: ${({ theme }) => theme.fonts.segoe};
  font-size: 12px;
  line-height: 16px/12px;
  color: ${({ theme }) => theme.colors.textThird};

`;
